import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "../styles/login.css";

const Register = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");  // Pour afficher l'erreur
  const [successMessage, setSuccessMessage] = useState("");  // Pour afficher un message de succès
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();

    // Vérification si les mots de passe correspondent
    if (password !== confirmPassword) {
      setErrorMessage("Passwords do not match!");
      return;
    }

    // Essayer de s'enregistrer
    try {
      const response = await axios.post("http://localhost:8080/auth/register", {
        email,
        password,
      });

      // Si l'inscription a réussi
      if (response.data.success) {
        setSuccessMessage("Registration successful! You can now log in.");
        setErrorMessage("");  // Reset error message
        setTimeout(() => {
          navigate("/login", { state: { successMessage: "Registration successful!" } });
        }, 2000);
      } else {
        setErrorMessage(response.data.message);  // Afficher l'erreur si l'email est déjà pris
      }
    } catch (error) {
      console.error("Registration error:", error);
      setErrorMessage("An error occurred during registration. Please try again.");
    }
  };

  return (
    <>
    <Navbar /> 
    <div className="login-container">
      <div className="login-box">
        <h2>Register</h2>

        {/* Affichage du message de succès ou d'erreur */}
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}

        <form onSubmit={handleRegister}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
          <button type="submit" className="login-button">Register</button>
        </form>

        <p>
          Already have an account? <Link to="/login">Login</Link>
        </p>
      </div>
    </div>
    </>
  );
};

export default Register;
