import { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import "../styles/login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState(""); // Message d'erreur
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/auth/login", {
        email,
        password,
      });

      console.log("Réponse du serveur :", response.data);

      if (response.data.token) { // Vérifie si un token est bien retourné
        localStorage.setItem("token", response.data.token);
        navigate("/categories");
      } else {
        setErrorMessage("Invalid email or password"); // Affiche l'erreur
      }
    } catch (error) {
      console.error("Login failed", error);
      setErrorMessage("Error during login. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>

        {/* Affichage de l'erreur sous le titre */}
        {errorMessage && <p className="error-message">{errorMessage}</p>}

        {location.state?.successMessage && (
          <p className="success-message">{location.state.successMessage}</p>
        )}

        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <p className="forgot-password">
            <span onClick={() => navigate("/forgot-password")} className="link-text">
              Forgot password?
            </span>
          </p>

          <button type="submit" className="login-button">Login</button>
        </form>

        <p className="register-link">
          Don't have an account?{" "}
          <span onClick={() => navigate("/register")} className="link-text">
            Register
          </span>
        </p>

        <button className="back-button" onClick={() => navigate("/")}>Back</button>
      </div>
    </div>
  );
};

export default Login;
